//
// IMPORTS
//

import React from 'react'
import { connect } from 'react-redux'
import HelloStream from './HelloStream'
import PlotlyChart from './Plotly'
import {apiRequest} from '../utils/api'

//
// COMPONENT
//

const Demo = ({score, x}) => {
  return (
      <div style={{width: '100%'}}>
        <div className='row'>
          <div className='col-xs-6 col-sm-6 col-md-6 col-lg-6'>
            <p>Hello Stream</p>
            <button onClick={() => {
              // test api route and stream connection
              apiRequest('/hello')
            }}>Test the stream</button>
            <HelloStream />
          </div>
          <div className='col-xs-6 col-sm-6 col-md-6 col-lg-6'>
            <PlotlyChart
              x={x}
              y={score}
              name='Score'
              type='log'
              color='#0000ff'
            />
          </div>
        </div>
      </div>
    )
}

//
// CONNECT
//

function mapStateToProps ({demo: {hellos, score}}) {
  const x = [0]
  if (score.length > 1) {
    for (let i = 1; i < score.length; i++) {
      x.push(i)
    }
  }
  return {hellos, score, x}
}

export default connect(
  mapStateToProps,
  null
)(Demo)
