//
// IMPORTS
//

export const SAY_HELLO = 'SAY_HELLO'
export const ADD_SCORE = 'ADD_SCORE'

//
// INITIAL STATE
//

const intialState = {
  score: []
}

//
// REDUCER
//

export default function demo (state = intialState, action) {
  switch (action.type) {

    case SAY_HELLO:
      return Object.assign({}, state, {
        hellos: [...state.hellos, action.hello]
      })

    case ADD_SCORE:
      return Object.assign({}, state, {
        score: [...state.score, action.y]
      })

    default:
      return state
  }
}

//
// EXPORT
//

export function sayHello (hello) {
  return {
    type: SAY_HELLO,
    hello
  }
}

export function addScore (y) {
  return {
    type: ADD_SCORE,
    y
  }
}
