// IMPORT
import React from 'react'
import { render } from 'react-dom'
import { createStore } from 'redux'
import { Meteor } from 'meteor/meteor'
import rootReducer from './reducers'
import Root from './containers/Root'
import ReactDOM from 'react-dom'

// CREATE REDUX SOTRE
const store = createStore(
  rootReducer, {}, window.devToolsExtension ? window.devToolsExtension() : undefined
)

// APP

Meteor.startup(() => {
  // STREAMS
  Streamy.on('hello', function (data) {
    console.log('received by hello stream: ', data)
  })

  // mount app
	const reactDivElement = document.getElementById('render-target')
	if (reactDivElement) {
		ReactDOM.render(<Root store={store} />, reactDivElement)
	}
})

Meteor._debug = (function (super_meteor_debug) {
  return function (error, info) {
    if (info && _.has(info, 'msg'))
      super_meteor_debug("Streamy message is allowed!", info)
    else
      super_meteor_debug(error, info);
  }
})(Meteor._debug)
