//
// IMPORTS
//

import React from 'react'
import { connect } from 'react-redux'
import HelloStream from './HelloStream'
import PlotlyChart from './Plotly'

//
// COMPONENT
//

const Demo = () => {
  return (
      <div>
        <div className='row'>
          <div className='col-xs-6 col-sm-6 col-md-6 col-lg-6'>
            <p>Hello Stream</p>
            <button onClick={() => {
              // test api route and stream connection
              apiRequest('/hello')
            }}>Test the stream</button>
            <HelloStream />
          </div>
          <div className='col-xs-6 col-sm-6 col-md-6 col-lg-6'>
            <PlotlyChart
              x={x}
              y={scoreStream}
              name='Score'
              type='log'
              color='#0000ff'
              average={average}
              averageStream={averageStream}
            />
          </div>
        </div>
      </div>
    )
}

//
// CONNECT
//

function mapStateToProps ({data: {hellos}}) {
  return {hellos}
}

export default connect(
  mapStateToProps,
  null
)(Demo)
