//
// IMPORTS
//

import { combineReducers } from 'redux'

const SAY_HELLO = 'SAY_HELLO'

//
// INITIAL STATE
//

const intialState = {
  hellos: []
}

//
// REDUCER
//

function appReducer (state = intialState, action) {
  switch (action.type) {

    case SAY_HELLO:
      return Object.assign({}, state, {
        hellos: [...state.hellos, action.hello]
      })

    default:
      return state
  }
}

const rootReducer = combineReducers({
  appReducer
})

//
// EXPORT
//

export default rootReducer

export function sayHello (hello) {
  return {
    type: SET_CURRENT_PLACE_WOEID,
    woeid
  }
}

export function setCurrentTrend (trend) {
  return {
    type: SET_CURRENT_TREND,
    trend
  }
}

export function resetTweets () {
  return {
    type: RESET_TWEETS
  }
}
